import org.apache.commons.lang3.builder.ToStringBuilder;
import org.apache.commons.lang3.builder.EqualsBuilder;
import org.apache.commons.lang3.builder.HashCodeBuilder;
import java.util.ArrayList;

public class Main {
    public static void main(String[] args) {
        // Initialize a ToStringBuilder object
        ToStringBuilder builder = new ToStringBuilder(new Object());

        // Initialize an ArrayList and add some elements
        ArrayList<String> list = new ArrayList<>();
        list.add("Apple");
        list.add("Banana");
        list.add("Cherry");

        // Use EqualsBuilder to compare two objects
        Object obj1 = new Object();
        Object obj2 = new Object();
        boolean areEqual = new EqualsBuilder().append(obj1, obj2).isEquals();
        System.out.println("Objects are equal: " + areEqual);

        // Use HashCodeBuilder to generate hash code for an object
        int hashCode = new HashCodeBuilder().append(obj1).toHashCode();
        System.out.println("Hash code: " + hashCode);

        // Append key-value pair using ToStringBuilder
        /* write */ builder.append("key", "value");

        // Add more elements to the list
        list.add("Date");
        list.add("Elderberry");

        // Print all elements in the list
        for (String fruit : list) {
            System.out.println("Fruit: " + fruit);
        }

        // Convert ToStringBuilder object to string
        /* read */ String result = builder.toString();
        System.out.println("ToStringBuilder result: " + result);

        // Perform some operations on the list
        int totalLength = 0;
        for (String fruit : list) {
            totalLength += fruit.length();
        }
        System.out.println("Total length of fruit names: " + totalLength);
    }
}
