import org.apache.commons.lang3.text.StrBuilder;

public class Main {
    public static void main(String[] args) {
        StrBuilder strBuilder = new StrBuilder("Hello, World!");

        // Additional StrBuilder operations
        StrBuilder anotherStrBuilder = new StrBuilder("Goodbye, Universe!");
        anotherStrBuilder.append(" Have a nice day.");
        System.out.println(anotherStrBuilder.toString());

        // Manipulating the original StrBuilder
        strBuilder.insert(0, "Greetings! ");
        System.out.println(strBuilder.toString());

        // Perform the write operation
        /* write */ strBuilder.delete(5, 7);

        // More complex operations
        StrBuilder yetAnotherStrBuilder = new StrBuilder("Complexity is key.");
        yetAnotherStrBuilder.replace(0, 10, "Simplicity");
        yetAnotherStrBuilder.reverse();
        System.out.println(yetAnotherStrBuilder.toString());

        // Perform some unrelated calculations
        int sum = 0;
        for (int i = 1; i <= 10; i++) {
            sum += i;
        }
        System.out.println("Sum of numbers 1 to 10: " + sum);

        // Perform the read operation
        /* read */ String result = strBuilder.toString();
        System.out.println(result);

        // Additional string operations
        StrBuilder finalStrBuilder = new StrBuilder("Finale!");
        finalStrBuilder.append(" The end.");
        System.out.println(finalStrBuilder.toString());
    }
}
