import org.apache.commons.lang3.mutable.MutableDouble;
import org.apache.commons.lang3.mutable.MutableInt;
import org.apache.commons.lang3.mutable.MutableBoolean;

public class Main {
    public static void main(String[] args) {
        MutableDouble mutableDouble = new MutableDouble();
        MutableInt mutableInt = new MutableInt();
        MutableBoolean mutableBoolean = new MutableBoolean();

        // Set initial values
        mutableInt.setValue(10);
        mutableBoolean.setValue(true);

        // Perform some calculations
        int calculatedValue = mutableInt.getValue() * 2;
        System.out.println("Calculated Integer Value: " + calculatedValue);

        // Conditional logic based on boolean
        if (mutableBoolean.isTrue()) {
            System.out.println("Boolean is true, setting double value.");
            /* write */ mutableDouble.setValue(42.0);
        } else {
            System.out.println("Boolean is false, setting double value to zero.");
            mutableDouble.setValue(0.0);
        }

        // More operations
        mutableInt.setValue(calculatedValue + 5);
        System.out.println("Updated Integer Value: " + mutableInt.getValue());

        // Final read of the double value
        /* read */ double value = mutableDouble.getValue();
        System.out.println("Final Double Value: " + value);

        // Additional logic
        if (value > 40) {
            System.out.println("The double value is greater than 40.");
        } else {
            System.out.println("The double value is 40 or less.");
        }
    }
}
