import org.apache.commons.lang3.text.StrBuilder;
import java.util.ArrayList;
import java.util.HashMap;

public class Main {
    public static void main(String[] args) {
        // Initialize StrBuilder with a sample string
        StrBuilder strBuilder = new StrBuilder("Hello, World!");

        // Perform some unrelated operations
        ArrayList<Integer> numbers = new ArrayList<>();
        for (int i = 0; i < 10; i++) {
            numbers.add(i * 2);
        }

        HashMap<String, Integer> wordCounts = new HashMap<>();
        wordCounts.put("apple", 3);
        wordCounts.put("banana", 2);
        wordCounts.put("cherry", 5);

        for (String word : wordCounts.keySet()) {
            System.out.println("Count of " + word + ": " + wordCounts.get(word));
        }

        // Modify the original StrBuilder
        /* write */ strBuilder.replace(7, 12, "Java");

        // Perform more unrelated operations
        int sum = 0;
        for (int number : numbers) {
            sum += number;
        }
        System.out.println("Sum of numbers: " + sum);

        StringBuilder stringBuilder = new StringBuilder("Initial String");
        stringBuilder.append(" with more content");
        System.out.println(stringBuilder.toString());

        // Read the modified StrBuilder
        /* read */ String result = strBuilder.toString();
        System.out.println(result);
    }
}
