import org.apache.commons.lang3.text.StrBuilder;
import java.util.ArrayList;

public class Main {
    public static void main(String[] args) {
        StrBuilder strBuilder = new StrBuilder("Hello World");
        ArrayList<String> stringList = new ArrayList<>();

        // Manipulate the string with StrBuilder
        stringList.add(strBuilder.toString());
        strBuilder.append(" from Apache Commons");
        strBuilder.insert(0, "Greeting: ");
        stringList.add(strBuilder.toString());

        // Perform replace operation
        /* write */ strBuilder.replaceFirst("World", "Java");

        // More string manipulations
        strBuilder.append(" Library");
        strBuilder.deleteFirst("Hello");
        strBuilder.insert(0, "Welcome to ");
        stringList.add(strBuilder.toString());

        // Process the list of strings
        for (String s : stringList) {
            System.out.println("List Element: " + s);
        }

        // Read the final string after all manipulations
        /* read */ String result = strBuilder.toString();
        System.out.println("Final Result: " + result);

        // Additional operations
        ArrayList<Integer> numberList = new ArrayList<>();
        for (int i = 0; i < 5; i++) {
            numberList.add(i * 10);
        }
        for (int num : numberList) {
            System.out.println("Number: " + num);
        }
    }
}
