import org.apache.commons.lang3.text.StrBuilder;
import java.util.ArrayList;

public class Main {
    public static void main(String[] args) {
        StrBuilder strBuilder = new StrBuilder("Hello World");
        ArrayList<Integer> numbers = new ArrayList<>();

        // Initialize the ArrayList with some numbers
        for (int i = 0; i < 5; i++) {
            numbers.add(i * 10);
        }

        // Perform some operations on the ArrayList
        int sum = 0;
        for (Integer number : numbers) {
            sum += number;
        }

        System.out.println("Sum of numbers: " + sum);

        // Modify the StrBuilder object
        /* write */ strBuilder.replace(6, 11, "Java");

        // Perform more operations on the ArrayList
        ArrayList<String> stringList = new ArrayList<>();
        for (int i = 0; i < numbers.size(); i++) {
            stringList.add("Number: " + numbers.get(i));
        }

        for (String str : stringList) {
            System.out.println(str);
        }

        // Read the modified StrBuilder object
        /* read */ String result = strBuilder.toString();
        System.out.println(result);

        // Additional operations to make the program more complex
        StrBuilder strBuilder2 = new StrBuilder("Complex Program");
        strBuilder2.append(" - Extended");
        System.out.println(strBuilder2.toString());
    }
}
