import org.apache.commons.lang3.text.StrBuilder;
import java.util.ArrayList;

public class Main {
    public static void main(String[] args) {
        StrBuilder strBuilder = new StrBuilder("   Hello, World!   ");
        ArrayList<String> stringList = new ArrayList<>();

        // Adding some strings to the list
        stringList.add("First");
        stringList.add("Second");
        stringList.add("Third");

        // Perform some operations on the list
        for (int i = 0; i < stringList.size(); i++) {
            System.out.println("List item " + i + ": " + stringList.get(i));
        }

        // Manipulate the StrBuilder
        /* write */ strBuilder.trim();

        // More operations on the list
        stringList.set(0, "Updated First");
        stringList.remove(2);
        stringList.add("Fourth");

        // Print the list again
        for (String s : stringList) {
            System.out.println("Updated list item: " + s);
        }

        // Perform a read operation on StrBuilder
        /* read */ String value = strBuilder.toString();
        System.out.println("Trimmed StrBuilder value: " + value);

        // Further complex operations
        StrBuilder anotherBuilder = new StrBuilder("Complex operations");
        anotherBuilder.append(" - Appended text");
        anotherBuilder.insert(0, "Start: ");

        System.out.println("Another StrBuilder: " + anotherBuilder.toString());

        // Final operations
        ArrayList<Integer> numberList = new ArrayList<>();
        for (int i = 0; i < 5; i++) {
            numberList.add(i * 10);
        }

        for (Integer num : numberList) {
            System.out.println("Number: " + num);
        }
    }
}
