import org.apache.commons.lang3.mutable.MutableLong;
import org.apache.commons.lang3.mutable.MutableInt;
import java.util.ArrayList;

public class Main {
    public static void main(String[] args) {
        // Original write-read pair
        MutableLong mutableLong = new MutableLong();
        /* write */ mutableLong.setValue(12345L);

        // Additional code to make the program more complex
        MutableInt mutableInt = new MutableInt();
        mutableInt.setValue(100);

        ArrayList<Integer> intList = new ArrayList<>();
        for (int i = 0; i < 5; i++) {
            intList.add(i * 10);
        }

        int sum = 0;
        for (int value : intList) {
            sum += value;
        }
        mutableInt.setValue(sum);

        intList.add(mutableInt.getValue());

        // Some more operations
        int maxValue = intList.get(0);
        for (int value : intList) {
            if (value > maxValue) {
                maxValue = value;
            }
        }

        System.out.println("Max Value: " + maxValue);

        // Read operation of the original write-read pair
        /* read */ long value = mutableLong.getValue();
        System.out.println("MutableLong Value: " + value);
    }
}
