import org.apache.commons.lang3.builder.CompareToBuilder;
import java.util.ArrayList;
import java.util.Collections;

public class Main {
    public static void main(String[] args) {
        CompareToBuilder compareToBuilder = new CompareToBuilder();
        ArrayList<Integer> numbers = new ArrayList<>();

        // Adding numbers to the list
        for (int i = 0; i < 5; i++) {
            numbers.add(i * 2);
        }

        // Shuffle the list to make it more complex
        Collections.shuffle(numbers);

        // Append some values for comparison
        /* write */ compareToBuilder.append(5, 10);

        // Perform additional operations on numbers
        int sum = 0;
        for (int num : numbers) {
            sum += num;
        }
        System.out.println("Sum of numbers: " + sum);

        // Sorting the list
        Collections.sort(numbers);

        // Additional comparison using CompareToBuilder
        CompareToBuilder additionalCompare = new CompareToBuilder();
        additionalCompare.append(20, 20);
        int additionalComparisonResult = additionalCompare.toComparison();
        System.out.println("Additional Comparison Result: " + additionalComparisonResult);

        // Original read operation
        /* read */ int comparisonResult = compareToBuilder.toComparison();
        System.out.println("Comparison Result: " + comparisonResult);

        // Print sorted numbers
        System.out.println("Sorted numbers: " + numbers);
    }
}
