import org.apache.commons.lang3.text.StrBuilder;
import java.util.ArrayList;

public class Main {
    public static void main(String[] args) {
        // Initialize StrBuilder and perform operations
        StrBuilder strBuilder = new StrBuilder("Hello World");
        StrBuilder strBuilder2 = new StrBuilder("Goodbye World");

        // Perform some unrelated operations
        ArrayList<Integer> numbers = new ArrayList<>();
        for (int i = 0; i < 5; i++) {
            numbers.add(i * 2);
        }

        // Modify strBuilder2
        strBuilder2.append("!");
        System.out.println("strBuilder2: " + strBuilder2.toString());

        // Perform operations on numbers
        for (int num : numbers) {
            System.out.println("Number: " + num);
        }

        // Use write method on strBuilder
        /* write */ strBuilder.deleteFirst("Hello");

        // Additional unrelated operations
        int sum = 0;
        for (int num : numbers) {
            sum += num;
        }
        System.out.println("Sum of numbers: " + sum);

        // Use read method on strBuilder
        /* read */ String result = strBuilder.toString();
        System.out.println(result);

        // Further operations on strBuilder2
        strBuilder2.insert(0, "Greetings ");
        System.out.println("Modified strBuilder2: " + strBuilder2.toString());
    }
}
