import java.util.concurrent.DelayQueue;
import java.util.concurrent.Delayed;
import java.util.concurrent.TimeUnit;
import java.util.ArrayList;
import java.util.LinkedList;

class DelayedElement implements Delayed {
    private long startTime;
    private String data;

    public DelayedElement(long delay, String data) {
        this.startTime = System.currentTimeMillis() + delay;
        this.data = data;
    }

    @Override
    public long getDelay(TimeUnit unit) {
        long diff = startTime - System.currentTimeMillis();
        return unit.convert(diff, TimeUnit.MILLISECONDS);
    }

    @Override
    public int compareTo(Delayed o) {
        return Long.compare(this.startTime, ((DelayedElement) o).startTime);
    }

    public String getData() {
        return data;
    }
}

public class Main {
    public static void main(String[] args) throws InterruptedException {
        DelayQueue<DelayedElement> queue = new DelayQueue<>();
        DelayedElement element = new DelayedElement(1000, "Hello, World!");

        /* write */ queue.put(element);

        // Additional code to make the program more complex
        ArrayList<DelayedElement> list = new ArrayList<>();
        LinkedList<DelayedElement> linkedList = new LinkedList<>();

        for (int i = 0; i < 5; i++) {
            DelayedElement newElement = new DelayedElement(1000 + i * 500, "Element " + i);
            list.add(newElement);
            linkedList.add(newElement);
        }

        for (DelayedElement e : list) {
            queue.put(e);
        }

        for (DelayedElement e : linkedList) {
            queue.put(e);
        }

        /* read */ DelayedElement takenElement = queue.take();
        System.out.println("First taken element: " + takenElement.getData());

        // More additional code
        ArrayList<String> stringList = new ArrayList<>();
        for (int i = 0; i < 10; i++) {
            stringList.add("String " + i);
        }

        for (String s : stringList) {
            System.out.println(s);
        }

        // Taking more elements from the queue
        while (!queue.isEmpty()) {
            DelayedElement e = queue.take();
            System.out.println("Taken element: " + e.getData());
        }
    }
}
