public class Student {
    private String name;
    private Course course;

    public Student(String name, Course course) {
        this.name = name;
        this.course = course;
    }

    public void addScore(int score) {
        course.addScore(score);
    }

    public Course getCourse() {
        return course;
    }

    public String getName() {
        return name;
    }
}

