import java.util.concurrent.CopyOnWriteArrayList;
import java.util.ArrayList;
import java.util.LinkedList;

public class Main {
    public static void main(String[] args) {
        CopyOnWriteArrayList<Integer> list = new CopyOnWriteArrayList<>();
        ArrayList<Integer> list2 = new ArrayList<>();
        LinkedList<Integer> list3 = new LinkedList<>();

        // Adding elements to list2 and list3
        list2.add(10);
        list2.add(20);
        list3.add(30);
        list3.add(40);

        // Writing to the main list
        /* write */ list.add(1);

        // Performing some operations on list2 and list3
        for (Integer num : list2) {
            list.add(num);
        }

        for (Integer num : list3) {
            list.add(num);
        }

        // Reading from the main list
        /* read */ int value = list.get(0);
        System.out.println("Read value: " + value);

        // Printing all elements in the list
        System.out.println("All elements in the list:");
        for (Integer num : list) {
            System.out.println(num);
        }

        // Adding more elements to list2 and list3
        list2.add(50);
        list3.add(60);

        // Adding elements from list2 and list3 to the main list
        for (Integer num : list2) {
            list.add(num);
        }

        for (Integer num : list3) {
            list.add(num);
        }

        // Printing all elements in the list again
        System.out.println("All elements in the list after adding more elements:");
        for (Integer num : list) {
            System.out.println(num);
        }
    }
}
