import java.util.concurrent.CopyOnWriteArrayList;

public class Main {
    public static void main(String[] args) {
        CopyOnWriteArrayList<Integer> list = new CopyOnWriteArrayList<>();

        // Populating the list with initial data
        for (int i = 0; i < 10; i++) {
            list.add(i);
        }

        // Unrelated operations
        int unrelated = list.size() * 3 + 7;
        boolean flag = (unrelated % 4 == 0);

        // Conditional logic that may mislead
        if (flag) {
            list.remove(2);
        } else {
            list.add(10);
        }

        // Multiple misleading operations
        list.set(5, unrelated);
        list.add(unrelated % 5);
        list.remove(new Integer(1));

        // The write operation that affects the read point
        /* write */ list.set(1, 99);

        // Further unrelated operations
        list.add(flag ? 75 : 57);
        list.set(3, list.get(3) + 3);

        // The key read operation
        /* read */ int ret = list.get(1);

        System.out.println("Retrieved value: " + ret);
    }
}
