import java.util.LinkedList;
import java.util.ArrayList;

public class Main {
    public static void main(String[] args) {
        LinkedList<Integer> list = new LinkedList<>();
        ArrayList<Integer> arrayList = new ArrayList<>();

        // Add some initial elements to the LinkedList
        for (int i = 0; i < 5; i++) {
            list.addLast(i * 10);
        }

        // Perform some operations on the ArrayList
        for (int i = 0; i < 5; i++) {
            arrayList.add(i * 5);
        }

        // Write to the LinkedList
        /* write */ list.addFirst(100);

        // Perform some unrelated operations
        int sum = 0;
        for (int num : arrayList) {
            sum += num;
        }
        System.out.println("Sum of ArrayList elements: " + sum);

        // Read from the LinkedList
        /* read */ int value = list.getFirst();
        System.out.println("Value read from LinkedList: " + value);

        // Add more elements to the LinkedList
        for (int i = 0; i < 3; i++) {
            list.addLast(i * 20);
        }

        // Print all elements in the LinkedList
        System.out.println("All elements in LinkedList:");
        for (int num : list) {
            System.out.println(num);
        }
    }
}
