import java.util.Vector;
import java.util.ArrayList;
import java.util.LinkedList;

public class Main {
    public static void main(String[] args) {
        Vector vector = new Vector();
        ArrayList list = new ArrayList();
        LinkedList linkedList = new LinkedList();

        // Adding elements to the ArrayList and LinkedList
        list.add("Hello");
        linkedList.add(100);

        /* write */ vector.add(1);

        // Iterating through the LinkedList and adding elements to the ArrayList
        for (Object obj : linkedList) {
            list.add(obj);
        }

        // Adding elements from the ArrayList to the Vector
        for (Object obj : list) {
            vector.add(obj);
        }

        /* read */ int value = (Integer) vector.get(0);
        System.out.println(value);

        // Creating a new ArrayList and adding elements to it
        ArrayList<Integer> newList = new ArrayList<>();
        for (int i = 0; i < 5; i++) {
            newList.add(i * 10);
        }

        // Adding elements from the new ArrayList to the Vector
        for (int i = 0; i < newList.size(); i++) {
            vector.add(newList.get(i));
        }

        // Printing elements from the Vector
        for (int i = 0; i < vector.size(); i++) {
            System.out.println(vector.get(i));
        }
    }
}
