public class DataProcessor {
    private SynchronousQueue<Integer> queue;

    public DataProcessor(SynchronousQueue<Integer> queue) {
        this.queue = queue;
    }

    public void processData() {
        try {
            /* write */ queue.put(42);
        } catch (InterruptedException e) {
            e.printStackTrace();
        }
    }
}

