            list.add(i * 10);
            linkedList.add(i * 20);
        }

        for (int i = 0; i < list.size(); i++) {
            System.out.println("ArrayList value: " + list.get(i));
            System.out.println("LinkedList value: " + linkedList.get(i));
        }

        // Thread to read from the queue
