import java.util.concurrent.CopyOnWriteArraySet;
import java.util.ArrayList;
import java.util.LinkedList;

public class Main {
    public static void main(String[] args) {
        CopyOnWriteArraySet<Integer> set = new CopyOnWriteArraySet<>();
        ArrayList<Integer> list = new ArrayList<>();
        LinkedList<String> linkedList = new LinkedList<>();

        // Adding elements to the ArrayList
        list.add(5);
        list.add(15);
        list.add(25);

        // Adding elements to the LinkedList
        linkedList.add("Hello");
        linkedList.add("World");

        // Writing to the CopyOnWriteArraySet
        /* write */ set.add(10);

        // Performing some operations on the ArrayList
        for (int i = 0; i < list.size(); i++) {
            System.out.println("ArrayList value: " + list.get(i));
        }

        // Performing some operations on the LinkedList
        for (String str : linkedList) {
            System.out.println("LinkedList value: " + str);
        }

        // Reading from the CopyOnWriteArraySet
        /* read */ boolean containsValue = set.contains(10);
        System.out.println("CopyOnWriteArraySet contains 10: " + containsValue);

        // Adding more elements to the ArrayList
        list.add(35);
        list.add(45);

        // Printing the final state of the ArrayList
        for (int i = 0; i < list.size(); i++) {
            System.out.println("Final ArrayList value: " + list.get(i));
        }
    }
}
