import java.util.BitSet;

public class Main {
    public static void main(String[] args) {
        BitSet bitSet = new BitSet();

        // Initialize bitset with some values
        bitSet.set(10);
        bitSet.set(20);
        bitSet.set(30);

        // Perform various operations
        if (bitSet.get(15)) {
            bitSet.clear(10);
        } else {
            bitSet.set(19);
        }

        // Another misleading operation
        bitSet.flip(30);

        // Perform conditional write
        int hiddenTrigger = (5 * 3) % 2;
        if (hiddenTrigger == 1) {
            /* write */ bitSet.set(5);
        } else {
            bitSet.set(25);
        }

        // Additional operations
        BitSet anotherSet = new BitSet();
        anotherSet.set(5);
        bitSet.or(anotherSet);

        anotherSet.set(24, 28);

        // Confusing intermediate computation
        int mask = 2 * 2 * 2;
        anotherSet.and(bitSet);

        // Actual read point
        /* read */ boolean value = bitSet.get(5);

        // Output the value to demonstrate effect
        System.out.println("Read value from BitSet at index 5: " + value);
    }
}
