import java.util.BitSet;

public class Main {
    public static void main(String[] args) {
        BitSet bitSet = new BitSet();
        // Set some bits before the write point
        bitSet.set(0);
        bitSet.set(2);

        // Some unrelated operations
        int calculations = 42 * 10 - 5 + 7;
        int complexLogic = (calculations % 5 == 0) ? 4 : 1;

        // Alternate unrelated logic
        boolean flag = calculations > 100;
        if (flag) {
            bitSet.set(5);
            bitSet.clear(0);
        } else {
            bitSet.set(6);
        }

        // The crucial write point
        if (complexLogic == 1) {
            /* write */ bitSet.set(1);
        } else if (complexLogic == 4) {
            bitSet.clear(1);
        }

        // More operations to obfuscate the program
        bitSet.flip(3);
        bitSet.set(7, 10);

        // Attempt to confuse the correspondence
        if (!flag) {
            bitSet.clear(2);
            bitSet.clear(4);
        }

        // The read point
        /* read */ boolean ret = bitSet.get(1);

        // Output to verify the result
        System.out.println("Value at bit position 1: " + ret);
    }
}
