import java.util.Hashtable;

public class Main {
    public static void main(String[] args) {
        Hashtable<Integer, String> hashtable = new Hashtable<>();

        // Pre-inserting some values
        hashtable.put(1, "One");
        hashtable.put(2, "Two");
        hashtable.put(3, "Three");

        // Random seed and decision making
        int randomSeed = (int) (Math.random() * 50);

        // Misleading operations
        if (randomSeed > 20) {
            hashtable.put(4, "Ignore Four");
        } else {
            hashtable.put(5, "Ignore Five");
        }

        /* write */
        if (randomSeed % 2 == 0) {
            hashtable.put(2, "Two Updated");
        }

        // More misleading operations
        hashtable.remove(4);
        hashtable.put(6, "Ignore Six");

        /* read */
        String ret = hashtable.get(2);

        // Output
        System.out.println("Retrieved Value: " + ret);
    }
}
