import java.util.LinkedList;
import java.util.Queue;
import java.util.Stack;

public class Main {
    public static void main(String[] args) {
        LinkedList<Integer> list = new LinkedList<>();
        Queue<Integer> queue = new LinkedList<>();
        Stack<Integer> stack = new Stack<>();

        // Working with the stack
        stack.push(10);
        stack.push(20);
        stack.push(30);
        while (!stack.isEmpty()) {
            System.out.println("Stack pop: " + stack.pop());
        }

        // Working with the queue
        queue.add(100);
        queue.add(200);
        queue.add(300);
        while (!queue.isEmpty()) {
            System.out.println("Queue poll: " + queue.poll());
        }

        // Original write operation on LinkedList
        /* write */ list.addFirst(42);

        // Additional operations on LinkedList
        list.addLast(99);
        list.add(1, 55);
        System.out.println("LinkedList size: " + list.size());

        // Some unrelated logic
        int sum = 0;
        for (int i = 0; i < 10; i++) {
            sum += i;
        }
        System.out.println("Sum of first 10 numbers: " + sum);

        // Original read operation on LinkedList
        /* read */ int value = list.getFirst();
        System.out.println("First element in LinkedList: " + value);

        // More operations on LinkedList
        list.removeFirst();
        list.removeLast();
        System.out.println("LinkedList after removals: " + list);
    }
}
