import java.util.BitSet;

public class Main {
    public static void main(String[] args) {
        BitSet bitSet = new BitSet(5);
        int rand = 13579;

        // unrelated variable
        int calculation = rand * 2 / 3 + 7;

        // misleading modifications
        for (int i = 0; i < 5; i++) {
            bitSet.set(i, i % 2 == 0);
        }

        // conditional logic
        if (calculation % 2 == 0) {
            bitSet.set(1, false); // has no effect on read point
        } else {
            /* write */ bitSet.set(2);
        }

        // other operations unrelated to read point
        if (bitSet.cardinality() > 2) {
            bitSet.clear(3);
        }

        // another unrelated modification
        bitSet.flip(4);

        /* read */ boolean ret = bitSet.get(2);

        // print to demonstrate the final value
        System.out.println("Value at bit 2: " + ret);
    }
}
